import { useState } from 'react'

const Button = ({ handleClick, text }) => {
  return (
    <button onClick={handleClick}>{text}</button>
  )
}

const App = () => {
  // save clicks of each button to its own state
  const [good, setGood] = useState(0)
  const [neutral, setNeutral] = useState(0)
  const [bad, setBad] = useState(0)

  const setTo = (increment, value) => () => increment(value)

  return (
    <div>
      <h1>give feedback</h1>
      <Button handleClick={setTo(setGood, good + 1)} text="good" />
      <Button handleClick={setTo(setNeutral, neutral + 1)} text="neutral" />
      <Button handleClick={setTo(setBad, bad + 1)} text="bad" />

      <h1>statistics</h1>
      <div>good: {good}</div> 
      <p>neutral: {neutral}</p> 
      <p>bad: {bad}</p> 
    </div>
  )
}

export default App
